import { tickets } from "../scripts/data";

let arr = [];
let sortedArr = [];

tickets().then((data) => {
  data.forEach((element) => {
    arr.push(element.price);
  });
});

function comp(a, b) {
  return a - b;
}

sortedArr = arr.sort((a, b) => {
  return b - a;
});

sortedArr.sort();

export default sortedArr;
