import React from "react";
import "./component-styles/sort.css";
import { useEffect, useState } from "react/cjs/react.production.min";
//import Sort from "./Sort";
import TicketList from "./TicketList";

class Child extends Component {
  render() {
    return (
      <div>
        <div style={{ ...style.topLabel, color: primaryColor }}>I am Child</div>
        <input
          style={style.textBox}
          type="text"
          placeholder="Write text"
          onChange={(e) => this.props.updateTextCB(e.target.value)}
        />
      </div>
    );
  }
}

export default class Flights extends Component {
  constructor(props) {
    super(props);
    this.state = { text: "Initial Text" };
    this.updateText1 = this.updateText1;
  }
  updateText1 = (text) => {
    this.setState({ text });
  };
  render() {
    return (
      <div>
        <div>I am Parent</div>
        <div style={style.label}>{this.state.text}</div>
        <Child updateTextCB={this.updateText1} />
      </div>
    );
  }
}
