export default class TicketList extends React.Component {
  state = {
    loading: true,
    ticket: null,
    displayed: 0,
  };

  async componentDidMount() {
    let arr = [];
    const res = await fetch("json/tickets.json");
    const data = await res.json();
    arr = sort(0, data);

    for (let i = this.state.displayed; i < 5; i++) {
      arr.push(arr[i]);
    }
    this.setState({
      ticket: arr,
      loading: false,
      displayed: (this.state.displayed += 5),
    });
  }

  render() {
    return (
      <div>
        {this.state.loading || !this.state.ticket ? (
          <div>loading...</div>
        ) : (
          <div>
            <ul>
              {this.state.ticket.map((e) => {
                return (
                  <li key={e.id}>
                    <FlightTicket ticket={e} />
                  </li>
                );
              })}
            </ul>
          </div>
        )}
      </div>
    );
  }
}

function sort(type, data) {
  let arr = data;

  switch (type) {
    case 0:
      {
        arr.sort((a, b) => {
          return a.price - b.price;
        });
      }
      break;
    default:
      return arr;
  }

  return arr;
}
