import { tickets } from "../scripts/data";

let arr = [];
let sortedArr = [];

tickets().then((data) => {
  data.forEach((element) => {
    arr.push(element);
  });
});

function priceComperator(a, b) {
  return b.price - a.price;
}

sortedArr = arr.sort(priceComperator);

export default sortedArr;
