export const tickets = () =>
  fetch("json/tickets.json").then((res) => res.json());
export const segments = () =>
  fetch("json/segments.json").then((res) => res.json());

async function fetchData(path) {
  try {
    const response = await fetch(path);
    const exam = await response.json();
    return exam;
  } catch (error) {
    console.error(error);
  }
}

export const companies = fetchData("json/companies.json");
