class Sort extends Component {
  render() {
    return (
      <div className="SortMenu">
        <div className="SortOption" onClick={(e) => this.props.func(0)}>
          <h3>Самый дешевый</h3>
        </div>
        <div className="SortOption" onClick={props.func(1)}>
          <h3>Самый быстрый</h3>
        </div>
        <div className="SortOption">
          <h3>Оптимальный</h3>
        </div>
      </div>
    );
  }
}

export default class Flights extends React.Component {
  state = {
    sortType: 0,
  };

  updateSort = (sortType) => {
    this.setState({ sortType });
    console.log(this.state.sortType);
  };

  render() {
    return (
      <div className="Flights">
        <Sort func={this.updateSort} />
        <TicketList />
      </div>
    );
  }
}
